export declare const FlowEditorBoardCss = "\n:root {\n  --color-panel-paper: #484848;\n  --color-panel-default: #1e1e1e;\n  --color-panel-dot: #1e1e1e;\n}\n\nhtml, body {\n  margin: 0;\n  padding: 0;\n  width: 100vw;\n  height: 100vh;\n  display: block;\n}\n\n.panel-wrapper {\n  z-index: 0;\n  width: 100vw;\n  height: 100vh;\n  display: block;\n  position: fixed;\n  overflow: hidden;\n  background-image: radial-gradient(var(--color-panel-dot) 5%, var(--color-panel-default) 5%);\n}\n\n.panel {\n  top: 0;\n  left: 0;\n  right: 0;\n  bottom: 0;\n  z-index: 0;\n  overflow: auto;\n  position: fixed;\n  background-color: transparent;\n}\n\n\n\n/* Ajusta o scroll de todos os elementos. */\n\n::-webkit-scrollbar {\n  width: 8px;\n  height: 8px;\n}\n\n::-webkit-scrollbar-corner {\n  background-color: transparent;\n}\n\n::-webkit-scrollbar:horizontal {\n  height: 16px;\n}\n\n::-webkit-scrollbar-button {\n  display: none;\n}\n\n::-webkit-scrollbar-track {\n  background-color: transparent;\n}\n\n::-webkit-scrollbar-thumb {\n  border-radius: 4px;\n  background-color: var(--color-panel-paper);\n}\n";
