export declare const DraggableContainerCss = "\n.draggable-container {\n  top: 0;\n  left: 0;\n  z-index:1;\n  cursor: move;\n  display: flex;\n  user-select: none;\n  position: absolute;\n  pointer-events: none;\n  flex-direction: column;\n  border: thin solid transparent;\n}\n\n.draggable-container-content {\n  flex: 1;\n  display: flex;\n  overflow: auto;\n  border-radius: 4px;\n  pointer-events: auto;\n  flex-direction: column;\n}\n\n.draggable-container-content::-webkit-scrollbar:horizontal {\n  height: 8px;\n}\n\n.draggable-container-input {\n  width: 10px;\n  left: -17px;\n  height: 10px;\n  cursor: crosshair;\n  border-radius: 50%;\n  position: absolute;\n  pointer-events: auto;\n  border: 2px solid green;\n  background-color: darkgreen;\n}\n.draggable-container-input:hover {\n  background-color: green;\n}\n\n.draggable-container-output {\n  width: 10px;\n  right: -17px;\n  height: 10px;\n  cursor: crosshair;\n  border-radius: 50%;\n  position: absolute;\n  pointer-events: auto;\n  transition: all .1s;\n  border: 2px solid crimson;\n  background-color: darkslategray;\n}\n.draggable-container-output:hover {\n  background-color: crimson;\n}\n\n.draggable-container-input[data-is-line-dragging=true], .draggable-container-output[data-is-line-dragging=true] {\n  border: 2px solid lightgreen;\n}\n\n.draggable-container-input[data-is-line-dragging=true]:hover, .draggable-container-output[data-is-line-dragging=true]:hover {\n  background-color: lightgreen;\n}\n";
